def get_legendary_npc_for_player(player_level, current_world=None):
    """Retorna o NPC lendÃ¡rio do reino atual do jogador."""
    npc_keys = sorted(LEGENDARY_NPCS.keys())
    reino = current_world if current_world is not None else player_level
    best = npc_keys[0]
    for k in npc_keys:
        if k <= reino:
            best = k
        else:
            break
    return LEGENDARY_NPCS.get(best)

def calculate_npc_stats(npc_data, player_level):
    """Calcula os stats do NPC lendÃ¡rio baseado no nÃ­vel do jogador"""
    base = npc_data["base_level"]
    effective_level = max(base, player_level)
    atk = int((effective_level * 3 + 50) * npc_data["atk_mult"])
    hp = int((effective_level * 8 + 200) * npc_data["hp_mult"])
    return atk, hp

def has_legendary_companion(player):
    """Verifica se o jogador tem um NPC lendÃ¡rio como companheiro"""
    return player.get("legendary_companion") is not None

def get_legendary_companion(player):
    """Retorna os dados do companheiro lendÃ¡rio do jogador"""
    comp_id = player.get("legendary_companion")
    if not comp_id:
        return None
    for npc in LEGENDARY_NPCS.values():
        if npc["id"] == comp_id:
            return npc
    return None

