def build_map_embed(player, page: int):
    """ConstrÃ³i o embed do mapa para uma pÃ¡gina/capÃ­tulo especÃ­fico."""
    chap = MAP_CHAPTER_KINGDOMS.get(page)
    page_data = MAP_PAGES.get(page)
    if not chap or not page_data:
        return None, None

    unlocked_worlds = set(player.get("worlds", [1]))
    # Usar current_world real do jogador, nÃ£o o maior desbloqueado
    current_world = player.get("current_world", max(unlocked_worlds))
    rng_min, rng_max = page_data["range"]
    disc_map = player.get("discovered_map", {})

    # Verifica se o capÃ­tulo estÃ¡ desbloqueado
    if page == 1:
        chapter_unlocked = True
    else:
        chapter_unlocked = any(
            (w_id is not None and w_id >= rng_min and w_id <= rng_max)
            for w_id, _, _ in chap["reinos"]
            if w_id in unlocked_worlds
        )

    color = chap["color"]
    embed = discord.Embed(
        title=chap["title"],
        description=f"*{chap['subtitle']}*",
        color=color
    )

    if not chapter_unlocked and page > 1:
        lock_msg = MAP_CYCLE_LOCK_MSG.get(page, "ğŸ”’ SeÃ§Ã£o bloqueada.")
        embed.add_field(name="ğŸ”’ Bloqueado", value=lock_msg, inline=False)
    else:
        fields_used = 0

        for entry in chap["reinos"]:
            w_id, emoji, name = entry
            if w_id is None:
                # Separador / dimensÃ£o â€” linha Ãºnica sem campo separado
                continue

            if w_id not in unlocked_worlds:
                # Mostrar prÃ³ximos reinos como bloqueados (sÃ³ os prÃ³ximos)
                if w_id <= current_world + 20:
                    embed.add_field(
                        name=f"ğŸ”’ {emoji} {name}",
                        value="*Derrote o boss do reino anterior para desbloquear.*",
                        inline=False
                    )
                    fields_used += 1
                continue

            # Reino desbloqueado â€” mostrar com locais descobertos
            # Seta mostra onde o jogador ESTÃ DE FATO (current_world), nÃ£o onde foi desbloqueado
            is_here = (w_id == current_world)
            # Se current_world nÃ£o estÃ¡ listado explicitamente neste capÃ­tulo, 
            # mostrar seta no reino mais prÃ³ximo abaixo
            if not is_here and current_world in unlocked_worlds:
                next_w_ids = [e[0] for e in chap["reinos"] if e[0] is not None and e[0] > w_id]
                is_here = (current_world >= w_id and (not next_w_ids or current_world < min(next_w_ids)))
            marker = " **â† vocÃª estÃ¡ aqui**" if is_here else ""
            known_ids = disc_map.get(str(w_id), [])
            world_map_data = MAP_LOCATIONS.get(w_id, {})
            world_locs = world_map_data.get("locations", [])

            loc_lines = []
            for loc in world_locs:
                is_visible = loc.get("discovered", False) or loc["id"] in known_ids
                if is_visible:
                    icon = MAP_TYPE_ICONS.get(loc["type"], "ğŸ“")
                    here = " ğŸ“Œ" if is_here else ""
                    loc_lines.append(f"{icon} {loc['name']}{here}")
