def get_dungeon_difficulty_multiplier(player):
    """Dungeons secretas ficam mais difÃ­ceis conforme o nÃ­vel â€” mas com cap razoÃ¡vel"""
    level = player.get("level", 1)
    # Escalamento suave: +2% por nÃ­vel, mÃ¡ximo 5x (era +5% sem cap = impossÃ­vel em lv 100+)
    return min(5.0, 1.0 + (level * 0.02))


