@bot.listen("on_message")
async def handle_afk_farm(message):
    if message.author.bot:
        return
    if message.channel.name != CANAL_BETA and message.channel.id not in MUNDO_PROPRIO_CHANNELS.values():
        return
    content = message.content.lower().strip()
    uid = str(message.author.id)

    if content in ["farm afk", "iniciar afk", "afk", "modo afk", "coletar auto", "auto coletar", "coleta automatica", "coleta automÃ¡tica"]:
        player = get_player(uid)
        if not player:
            await message.channel.send("âŒ Crie seu personagem primeiro!")
            return
        if player.get("afk_farming"):
            # Coletar tudo acumulado
            elapsed = int(time.time()) - player.get("afk_start", int(time.time()))
            minutes = max(1, elapsed // 60)
            hours = elapsed // 3600

            # XP: 2 por minuto, mÃ¡x 600
            xp_earned = max(1, min(minutes * 2, 600))

            # Coins: 1 por minuto, mÃ¡x 300
            coins_earned = max(1, min(minutes * 1, 300))

            # Recursos: 1 por 5 minutos, mÃ¡x 20
            world = get_world(player["level"], player)
            resources_found = []
            resource_count = min(minutes // 5, 20)
            for _ in range(resource_count):
                if world.get("resources"):
                    resources_found.append(random.choice(world["resources"]))

            # Equipamentos: chance crescente com tempo (1% por hora, mÃ¡x 15%)
            equip_found = []
            equip_chance = min(0.01 * max(1, hours), 0.15)
            for _try in range(2):  # tenta arma e armadura
                if random.random() < equip_chance:
                    _is_weapon = (_try == 0)
                    _pool_all = ITEMS["weapons"] if _is_weapon else ITEMS["armor"]
                    _r = random.random()
                    if _r < 0.60:
                        _pool = [i for i in _pool_all if i["rarity"] == "Comum"]
                    elif _r < 0.90:
                        _pool = [i for i in _pool_all if i["rarity"] == "Incomum"]
                    else:
                        _pool = [i for i in _pool_all if i["rarity"] == "Raro"]
                    if _pool:
                        _item = random.choice(_pool)
                        equip_found.append(_item)
                        resources_found.append(_item["name"])

            # Salvar tudo
            player["afk_farming"] = 0
            player["afk_start"] = 0
            for r in resources_found:
                player["inventory"].append(r if isinstance(r, str) else r["name"])
            player["coins"] = player.get("coins", 0) + coins_earned
            save_player_db(uid, player)
            leveled = add_xp(uid, xp_earned, bypass_boss_gate=False)

            h = elapsed // 3600
            m = (elapsed % 3600) // 60
            time_str = f"{h}h {m}m" if h > 0 else f"{m}m"

            embed = discord.Embed(
                title="â¹ï¸ Coleta AutomÃ¡tica Encerrada!",
                description=f"*'VocÃª retorna carregado de recursos apÃ³s {time_str} de trabalho!'*",
                color=discord.Color.gold()
            )
            embed.add_field(name="â±ï¸ Tempo", value=f"`{time_str}`", inline=True)
            embed.add_field(name="â­ XP", value=f"`+{xp_earned}`", inline=True)
            embed.add_field(name="ğŸ’° Coins", value=f"`+{coins_earned}`", inline=True)

            if resources_found:
                res_str = "\n".join([f"â€¢ {r}" if isinstance(r, str) else f"â€¢ **{r['name']}** ({r['rarity']})" for r in (resources_found[:8])])
                embed.add_field(name=f"ğŸ’ Itens Coletados ({len(resources_found)})", value=res_str, inline=False)
            else:
                embed.add_field(name="ğŸ’ Itens", value="*Nada coletado â€” fique mais tempo para acumular recursos!*", inline=False)

            if equip_found:
                eq_str = "\n".join([f"{'ğŸ—¡ï¸' if e in ITEMS['weapons'] else 'ğŸ›¡ï¸'} **{e['name']}** ({e['rarity']})" for e in equip_found])
                embed.add_field(name="âœ¨ Equipamentos Encontrados!", value=eq_str, inline=False)

            if leveled:
                p2 = get_player(uid)
                embed.add_field(name="ğŸ†™ Level Up!", value=f"**NÃ­vel {p2['level']}**", inline=False)
            embed.set_footer(text="Use 'farm afk' novamente para nova sessÃ£o. Quanto mais tempo, mais recompensas!")
            await message.channel.send(embed=embed)
        else:
            player["afk_farming"] = 1
            player["afk_start"] = int(time.time())
            save_player_db(uid, player)
            world = get_world(player["level"], player)
            embed = discord.Embed(
                title="ğŸŒ™ Coleta AutomÃ¡tica Iniciada!",
                description=f"*'VocÃª comeÃ§a a coletar recursos automaticamente em **{world['name']}**...'*",
                color=discord.Color.blue()
            )
            embed.add_field(name="â­ XP", value="`~2 por minuto` (mÃ¡x 600)", inline=True)
            embed.add_field(name="ğŸ’° Coins", value="`~1 por minuto` (mÃ¡x 300)", inline=True)
            embed.add_field(name="ğŸ’ Recursos", value="`1 a cada 5 min` (mÃ¡x 20)", inline=True)
            embed.add_field(name="âœ¨ Equipamentos", value="`Chance cresce com o tempo` (mÃ¡x 15%)", inline=True)
            embed.add_field(name="ğŸ’¡ Dica", value="Quanto mais tempo ficar no AFK, maior a chance de equipamentos raros!", inline=False)
            embed.set_footer(text="Use 'farm afk' novamente ao voltar para resgatar tudo!")
            await message.channel.send(embed=embed)

    elif content in ["ver clima", "clima", "tempo", "clima atual"]:
        weather = CURRENT_WEATHER
        wdata = WEATHER_TYPES.get(weather["type"], WEATHER_TYPES["sol"])
